import React from 'react';
import './App.css';
import Footer from './Footer';
import Header from './Header';
import About from './About';
import Home from './Home';
import Missing from './Missing';
import Nav from './Nav';
import NewPost from './NewPost';
import PostPage from './PostPage';
import { Routes, Route } from 'react-router-dom';
import { useState } from 'react';

function App() {
  const [posts, setPosts] = useState([
    {
      id: 1,
      title: "My First Post",
      datetime: "July 01, 2021 11:17:36 AM",
      body: "Made a video about Tesla Q1 results."
    },
    {
      id: 2,
      title: "Exploring the City",
      datetime: "August 15, 2021 09:45:22 AM",
      body: "Went on a city tour and captured some amazing photos."
    },
    {
      id: 3,
      title: "Tech Conference Highlights",
      datetime: "September 23, 2021 02:30:10 PM",
      body: "Attended a tech conference and learned about the latest in AI and machine learning."
    },
    {
      id: 4,
      title: "Recipe for Success",
      datetime: "October 05, 2021 06:14:55 PM",
      body: "Shared my favorite recipe for homemade pasta."
    },
    {
      id: 5,
      title: "Weekend Getaway",
      datetime: "November 20, 2021 08:22:34 AM",
      body: "Spent the weekend at a beautiful countryside resort."
    }
  ]);
  const [searchResults, setSearchResults] = useState('');
  const [search, setSearch] = useState('');

  return (
    <div className="App">
      <Header title="MG Social Media" />
      <Nav
        search={search}
        setSearch={setSearch}
      />
      <div className="content">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="about" element={<About />} />
          <Route path="new-post" element={<NewPost />} />
          <Route path="post/:id" element={<PostPage />} />
          <Route path="*" element={<Missing />} />
        </Routes>
      </div>
      <Footer />
    </div>
  );
}

export default App;
